import React, { useEffect, useState } from 'react';
import MessageList from './MessageList';
import MessageDetail from './MessageDetail';
import axios from 'axios';

function MessageBoard() {
  const [messages, setMessages] = useState([]);
  const [selectedMessage, setSelectedMessage] = useState(null);

  useEffect(() => {
    // 초기 메시지 리스트를 불러오는 함수 호출
    fetchMessageList();
  }, []);

  // 메시지 리스트를 불러오는 함수
  const fetchMessageList = async () => {
    try {
      const response = await axios.get("/api/messages");
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching message list:', error);
    }
  };

  // 메시지를 선택하는 함수
  const selectMessage = (message) => {
    setSelectedMessage(message);
    // 메시지 내용을 불러오는 함수 호출
    fetchMessageContent(message.message_no);
  };

  // 메시지 내용을 불러오는 함수
  const fetchMessageContent = async (messageNo) => {
    try {
      const response = await axios.get(`/api/messages/${messageNo}`);
      // 메시지 내용을 업데이트
      // 이 함수로 메시지 내용을 가져올 때마다 스크롤를 맨 아래로 가게 함
      document.querySelector(".msg_history").scrollTop = document.querySelector(".msg_history").scrollHeight;
    } catch (error) {
      console.error('Error fetching message content:', error);
    }
  };

  // 메시지를 전송하는 함수
  const sendMessage = async (messageNo, receiverNo, content) => {
    try {
      await axios.post("/api/messages/send", {
        messageNo,
        receiverNo,
        content
      });
      // 메시지 입력칸 비우기
      document.querySelector(".write_msg").value = "";
      // 메시지 리스트 및 내용 리로드
      fetchMessageList();
      fetchMessageContent(messageNo);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div>
      <h2>Message Board</h2>
      <div className="msg-container">
        <div className="messaging">
          <div className="inbox_msg">
            <div className="inbox_people">
              <div className="headind_srch">
                <div className="recent_heading">
                  <h4>Recent</h4>
                </div>
                <div className="srch_bar">
                  <div className="stylish-input-group">
                    <input type="text" className="search-bar" placeholder="Search" />
                    <span className="input-group-addon">
                      <button type="button"><i className="fa fa-search" aria-hidden="true"></i></button>
                    </span>
                  </div>
                </div>
              </div>
              <MessageList messages={messages} onSelectMessage={selectMessage} />
            </div>
            <div className="mesgs">
              <div className="msg_history" name="contentList">
                <MessageDetail message={selectedMessage} />
              </div>
              <div className="send_message">
                <MessageWrite onSendMessage={sendMessage} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MessageBoard;
