import React, { useEffect, useState } from 'react';

function MessageDetail({ message }) {
  const [messageDetail, setMessageDetail] = useState(null);

  useEffect(() => {
    if (message) {
      // API 호출 -> 쪽지 상세내용 불러와햐함
      fetch(`/api/messages/${message.message_no}`)
        .then(response => response.json())
        .then(data => setMessageDetail(data))
        .catch(error => console.error('Error fetching message detail:', error));
    }
  }, [message]);

  return (
    <div className="MessageDetail">
      {messageDetail ? (
        <>
          <h2>{messageDetail.title}</h2>
          <h3>{messageDetail.sender_no}</h3>
          <p>{messageDetail.content}</p>
          <button>채팅</button>
          <button>답장</button>
        </>
      ) : (
        <div>쪽지를 선택하세요</div>
      )}
    </div>
  );
}

export default MessageDetail;
