import React from 'react';
import './messageList.css'; 

function MessageList({ onSelectMessage }) {
  // 임시 데이터
  const messages = [
    {
      message_no: 1,
      sender_no: 1,
      receiver_no: 2,
      message_content: "첫 번째 메시지입니다."
    },
    {
      message_no: 2,
      sender_no: 2,
      receiver_no: 1,
      message_content: "두 번째 메시지입니다."
    },
    {
      message_no: 3,
      sender_no: 1,
      receiver_no: 2,
      message_content: "세 번째 메시지입니다."
    }
  ];

  // 대화방 목록 생성
  const chatRooms = {};
  messages.forEach(message => {
    const chatKey = Math.min(message.sender_no, message.receiver_no) + '_' + Math.max(message.sender_no, message.receiver_no);
    if (!chatRooms[chatKey]) {
      chatRooms[chatKey] = [];
    }
    chatRooms[chatKey].push(message);
  });

  // 최근 메시지만 선택
  const recentMessages = Object.values(chatRooms).map(roomMessages => {
    return roomMessages[roomMessages.length - 1]; // 각 대화방의 마지막 메시지
  });

  return (
    <div className="message-list-container">
      {recentMessages.map((message, index) => (
        <div key={index} className="chat-list-box" onClick={() => onSelectMessage(message)}>
          <div className="chat-list">
            <div className="chat-people">
              <div className="chat-ib">
                <h5>{message.sender_no === 1 ? 'Sender' : 'Receiver'}</h5>
                <p>{message.message_content}</p>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default MessageList;
