import React, { useState } from 'react';
import MessageList from './MessageList'; // Import MessageList component
import NewMessage from './NewMessage'; // Import NewMessage component
import axios from 'axios';

function ProfileToMessage() {
  const [messages, setMessages] = useState([]);
  const [selectedMessage, setSelectedMessage] = useState(null);

  const selectMessage = (message) => {
    setSelectedMessage(message);
  };

  // Rest of your code for fetching messages and sending new messages

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-4 border-right">
          <h2>나에게 온 쪽지</h2>
          <div className="msg-container">
            <div className="messaging">
              <div className="inbox_msg">
                <div className="inbox_people">
                  {/* Pass messages and selectMessage as props to MessageList component */}
                  <MessageList messages={messages} onSelectMessage={selectMessage} />
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-8">
          {selectedMessage ? (
            <div className="message-detail-container">
              {/* Render NewMessage component */}
              <NewMessage />
            </div>
          ) : (
            <div className="noMessage">함께 하고 싶은 <br/>여행 메이트에게 <br/>메시지를 보내보세요!</div>
          )}
        </div>
      </div>
    </div>
  );
}

export default ProfileToMessage;
