import React, { useState } from 'react';
import axios from 'axios';

function MessageWrite({ onSendMessage }) {
  const [newMessageContent, setNewMessageContent] = useState("");

  // 메시지 전송 함수
  const sendMessage = async () => {
    try {
      await axios.post("/api/messages/send", {
        content: newMessageContent
      });
      setNewMessageContent(""); // 메시지 전송 후 입력란 비우기
      onSendMessage(); // 메시지 전송 완료를 상위 컴포넌트에 알리기
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="message-send-form">
      <textarea
        className="form-control"
        rows="3"
        value={newMessageContent}
        onChange={(e) => setNewMessageContent(e.target.value)}
      />
      <button className="btn btn-primary" onClick={sendMessage}>전송</button>
    </div>
  );
}

export default MessageWrite;
