import React, { useEffect, useState } from 'react';
import MessageList from './MessageList';
import MessageDetail from './MessageDetail';
import MessageWrite from './MessageWrite'; // MessageWrite 컴포넌트 추가
import axios from 'axios';

function MessageBoard() {
  const [messages, setMessages] = useState([]);
  const [selectedMessage, setSelectedMessage] = useState(null);

  useEffect(() => {
    // 초기 메시지 리스트를 불러오는 함수 호출
    fetchMessageList();
  }, []);

  // 메시지 리스트를 불러오는 함수
  const fetchMessageList = async () => {
    try {
      const response = await axios.get("/api/messages");
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching message list:', error);
    }
  };

  // 메시지를 선택하는 함수
  const selectMessage = (message) => {
    setSelectedMessage(message);
  };

  // 메시지 전송 완료 후 호출되는 함수
  const handleSendMessage = () => {
    fetchMessageList(); // 메시지 리스트 다시 불러오기
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-4 border-right">
          <h2>나에게 온 쪽지</h2>
          <div className="msg-container">
            <div className="messaging">
              <div className="inbox_msg">
                <div className="inbox_people">
                  <div className="headind_srch">
                    <div className="recent_heading">
                    </div>
                    <div className="srch_bar">
                      <div className="input-group stylish-input-group">
                        <input type="text" className="form-control search-bar" placeholder="Search" />
                        <span className="input-group-addon">
                          <button type="button" className="btn btn-primary">검색</button>
                        </span>
                      </div>
                    </div>
                  </div>
                  <MessageList messages={messages} onSelectMessage={selectMessage} />
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-8">
          {selectedMessage && (
            <div className="message-detail-container">
              <h2>Message Detail</h2>
              <MessageDetail message={selectedMessage} />
            </div>
          )}
          {/* MessageWrite 컴포넌트 추가 */}
          <MessageWrite onSendMessage={handleSendMessage} />
        </div>
      </div>
    </div>
  );
}

export default MessageBoard;
