import React, { useEffect, useState } from 'react';
import './comment.css';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Comment = ({ comments, setComments }) => {
  const { boardNo } = useParams();
  const [commentList, setCommentList] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [replyText, setReplyText] = useState('');
  const [replyingTo, setReplyingTo] = useState(null);

  useEffect(() => {
    axios.get("/findBoardRepls/" + boardNo).then((res) => {
      setCommentList(res.data);
    });
  }, [boardNo]);

  const replList = (comment) => {
    if (comment.boardrepl_repl_seq === 0) {
      return (
        <div key={comment.replauthor_no} className="comment">
          <div className="comment-profile">
            <span className="comment-author">{comment.replauthor_nickname}</span>
            <p className="comment-text">{comment.boardrepl_content}</p>
            <div className="replies">
              {replyingTo === comment.boardrepl_no && (
                <div className="reply-input">
                  <textarea
                    placeholder="대댓글을 작성해주세요."
                    value={replyText}
                    onChange={(e) => setReplyText(e.target.value)}
                  ></textarea>
                  <button className="reply-button" onClick={() => handleAddReply(comment)}>대댓글 작성</button>
                </div>
              )}
            </div>
          </div>
        </div>
      );
    }
  };

  const handleCommentChange = (e) => {
    setNewComment(e.target.value);
  };

  const handleAddComment = () => {
    const newCommentObj = {
      replauthor_no: 1,
      boardrepl_content: newComment,
      replauthor_nickname: '홍시',
      boardrepl_repl_seq: 0,
      board_no: boardNo
    };
    axios.post("/postBoardRepl", newCommentObj);
    window.location.reload();
    setNewComment('');
  };

  const handleToggleReply = (commentId) => {
    setReplyingTo(commentId === replyingTo ? null : commentId);
    setReplyText(''); 
  };

  const handleAddReply = (comment) => {
    const newCommentObj = {
      replauthor_no: 1,
      boardrepl_content: replyText,
      replauthor_nickname: '홍시',
      boardrepl_repl_seq: 1,
      board_no: boardNo
    };
    axios.post("/postBoardRepl", newCommentObj);
    window.location.reload();
  };

  return (
    <div className="comment-section">
      <div className="comment-input">
        <textarea
          placeholder="댓글을 작성해주세요."
          value={newComment}
          onChange={handleCommentChange}
        ></textarea>
        <button className="comment-button" onClick={handleAddComment}>댓글 작성</button>
      </div>
      <div className="comments">
        {commentList.map((comment) => replList(comment))}
      </div>
    </div>
  );
};

export default Comment;
