import React from 'react';
import './messageDetail.css';

function MessageDetail({ messages }) {
  if (!messages || messages.length === 0) {
    return <div>No messages selected</div>;
  }

  return (
    <div className="message-detail-container">
      {messages.map((message, index) => (
        <div key={index} className="message-box">
          <p className="message-detail-text">Sender No: {message.sender_no}</p>
          <p className="message-detail-text">Content: {message.message_content}</p>
          <p className="message-detail-text">Send Date: {message.send_date}</p>
        </div>
      ))}
    </div>
  );
}

export default MessageDetail;
