import React, { useEffect, useState } from 'react';
import MessageList from './MessageList';
import MessageDetail from './MessageDetail'; // Import MessageDetail component
import NewMessage from './NewMessage'; 
import axios from 'axios';

function ProfileToMessage() {
  const [messages, setMessages] = useState([]);
  const [selectedMessage, setSelectedMessage] = useState(null);

  useEffect(() => {
    fetchMessageList();
  }, []);

  const fetchMessageList = async () => {
    try {
      const response = await axios.get("/api/messages");
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching message list:', error);
    }
  };

  const selectMessage = (message) => {
    setSelectedMessage(message);
  };

  const handleRoomSelect = (roomId) => {
    // Find the message associated with the selected room ID
    const selectedRoomMessages = messages.find(message => message.roomId === roomId);
    setSelectedMessage(selectedRoomMessages);
  };

  const handleDeleteRoom = (roomId) => {
    // Logic to delete the selected room
    console.log("Delete room:", roomId);
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-4 border-right">
          <h2>나에게 온 쪽지</h2>
          <div className="msg-container">
            <div className="messaging">
              <div className="inbox_msg">
                <div className="inbox_people">
                  <div className="headind_srch">
                    <div className="recent_heading">
                    </div>
                    <div className="srch_bar">
                      <div className="input-group stylish-input-group">
                        <input type="text" className="form-control search-bar" placeholder="Search" />
                        <span className="input-group-addon">
                          <button type="button" className="btn btn-primary">검색</button>
                        </span>
                      </div>
                    </div>
                  </div>
                  {/* Pass handleRoomSelect and handleDeleteRoom as props to MessageList */}
                  <MessageList messages={messages} onSelectMessage={selectMessage} onRoomSelect={handleRoomSelect} onDeleteRoom={handleDeleteRoom} />
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-8">   
          {selectedMessage ? (
            <MessageDetail message={selectedMessage} />
          ) : (
            <NewMessage /> 
          )}
        </div>
      </div>
    </div>
  );
}

export default ProfileToMessage;
