import React, { useState } from 'react';
import './messageList.css';

function MessageList() {
  // 임시 데이터
  const [messages, setMessages] = useState([
    {
      "message_no": 1,
      "message_content": "안녕하세요!",
      "sender_id": 1,
      "receiver_id": 2,
      "send_date": "2024-06-10"
    },
    {
      "message_no": 2,
      "message_content": "안녕하세요! 반가워요!",
      "sender_id": 2,
      "receiver_id": 1,
      "send_date": "2024-06-11"
    },
    {
      "message_no": 3,
      "message_content": "오늘 날씨가 정말 좋네요.",
      "sender_id": 1,
      "receiver_id": 2,
      "send_date": "2024-06-12"
    },
    {
      "message_no": 4,
      "message_content": "맞아요! 햇빛이 환하게 비추고 있어요.",
      "sender_id": 2,
      "receiver_id": 1,
      "send_date": "2024-06-12"
    }
  ]);

  return (
    <div className="message-list-container">
      {messages.map(message => (
        <div key={message.message_no} className="chat-list-box" onClick={() => onSelectMessage(message)}>
          <div className="chat-list">
            <div className="chat-people">
              <div className="chat-ib">
                <h5>{message.sender_id === 1 ? 'Sender' : 'Receiver'}</h5>
                <p>{message.message_content}</p>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default MessageList;
