import React, { useState } from 'react';

function MessageWrite() {
  const [formData, setFormData] = useState({
    receiver_no: '',
    subject: '',
    content: ''
  });

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    console.log('Submitted data:', formData);
    // 여기에 메시지를 전송하는 로직을 추가할 수 있습니다.
  };

  return (
    <div>
      <h2>메시지 작성</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="receiver_no">수신자 번호:</label>
          <input type="text" id="receiver_no" name="receiver_no" value={formData.receiver_no} onChange={handleChange} />
        </div>
        <div>
          <label htmlFor="subject">제목:</label>
          <input type="text" id="subject" name="subject" value={formData.subject} onChange={handleChange} />
        </div>
        <div>
          <label htmlFor="content">내용:</label>
          <textarea id="content" name="content" value={formData.content} onChange={handleChange}></textarea>
        </div>
        <button type="submit">전송</button>
      </form>
    </div>
  );
}

export default MessageWrite;
