import React from 'react';
import './messageList.css'; 

function MessageList({ onSelectMessage }) {
  // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
  const messages = [
    {
      message_no: 1,
      sender_no: 1,
      receiver_no: 2,
      message_content: "Ï≤´ Î≤àÏß∏ Î©îÏãúÏßÄÏûÖÎãàÎã§.",
      SEND_DATE:"2024-01-25"
    },
    {
      message_no: 2,
      sender_no: 2,
      receiver_no: 1,
      message_content: "Îëê Î≤àÏß∏ Î©îÏãúÏßÄÏûÖÎãàÎã§.",
      SEND_DATE:"2024-01-26"
    },
    {
      message_no: 3,
      sender_no: 1,
      receiver_no: 2,
      message_content: "ÏÑ∏ Î≤àÏß∏ Î©îÏãúÏßÄÏûÖÎãàÎã§.",
      SEND_DATE:"2024-01-26"
    },
    {
      message_no: 4,
      sender_no: 3,
      receiver_no: 5,
      message_content: "ÎÑ§ Î≤àÏß∏ Î©îÏãúÏßÄÏûÖÎãàÎã§.",
      SEND_DATE:"2024-01-28"
    },
        {
      message_no: 5,
      sender_no: 3,
      receiver_no: 1,
      message_content: "ÎÑ§ Î≤àÏß∏ Î©îÏãúÏßÄÏûÖÎãàÎã§.",
      SEND_DATE:"2024-01-15"
    }
  ];

  // ÎåÄÌôîÎ∞© Î™©Î°ù ÏÉùÏÑ±
  const chatRooms = {};
  messages.forEach(message => {
    if (message.sender_no === 1 || message.receiver_no === 1) { // sender_noÍ∞Ä 1Ïù¥Í±∞ÎÇò receiver_noÍ∞Ä 1Ïù∏ Í≤ΩÏö∞Îßå Ï≤òÎ¶¨
      const roomId = Math.min(message.sender_no, message.receiver_no) + '_' + Math.max(message.sender_no, message.receiver_no);
      if (!chatRooms[roomId]) {
        chatRooms[roomId] = [];
      }
      chatRooms[roomId].push(message);
    }
  });

  const handleRoomClick = (roomId) => {
    // ÏÑ†ÌÉùÎêú ÎåÄÌôîÎ∞©Ïùò Î™®Îì† Î©îÏãúÏßÄÎ•º Ï∂îÏ∂ú
    const selectedRoomMessages = chatRooms[roomId];
    // onSelectMessage Ìï®ÏàòÎ•º Ìò∏Ï∂úÌïòÏó¨ ÏÑ†ÌÉùÎêú ÎåÄÌôîÎ∞©Ïùò Î™®Îì† Î©îÏãúÏßÄÎ•º Ï†ÑÎã¨
    onSelectMessage(selectedRoomMessages);
  };
  
  const handleDeleteRoom = (roomId) => {
    // Ìï¥Îãπ ÎåÄÌôîÎ∞© ÏÇ≠Ï†ú Î°úÏßÅ Íµ¨ÌòÑ
    // ÏÑ†ÌÉùÎêú roomIdÏóê Ìï¥ÎãπÌïòÎäî ÎåÄÌôîÎ∞©ÏùÑ ÏÇ≠Ï†úÌïòÍ≥† UIÎ•º ÏóÖÎç∞Ïù¥Ìä∏
    console.log("Delete room:", roomId);
  };

  return (
    <div className="message-list-container">
      {Object.keys(chatRooms).map((roomId, index) => {
        const roomMessages = chatRooms[roomId];
        const lastMessage = roomMessages[roomMessages.length - 1];
        return (
          <div key={index} className="chat-list-box" onClick={() => handleRoomClick(roomId)}>
            <div className="chat-list">
              <div className="chat-people">
                <div className="chat-ib">
                  <h5>{lastMessage.sender_no === 1 ? `${lastMessage.sender_no}ÎãòÏù¥ Î≥¥ÎÇ∏ Î©îÏãúÏßÄ` : `${lastMessage.receiver_no}ÎãòÏù¥ Î≥¥ÎÇ∏ Î©îÏãúÏßÄ`}</h5>
                  <p>{lastMessage.message_content}</p>
                </div>
                <button className="delete-button" onClick={() => handleDeleteRoom(roomId)}>üóë</button>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}
export default MessageList;
